#!/usr/bin/env python3

import rclpy
from rclpy.node import Node
from std_msgs.msg import String 

class ByeWorlds(Node):
    def __init__(self):
        super().__init__('bye_worlds')
        self.get_logger().info(' bye Worlds! Node Started.')
        self.subscription = self.create_subscription(
            String,
            "chatter",
            self.listener_callback,
            10
            )
        self.subscription

    def listener_callback(self,data):
     #in data.data if the message strings first letter is a vowel print it
     if data.data[0].lower() in ['a', 'e', 'i', 'o', 'u']:
       self.get_logger().info(f'subscriber:"%s"' %data.data)
     

def main(args=None):
    rclpy.init(args=args)
    node = ByeWorlds()
    rclpy.spin(node)
    node.destroy_node()
    rclpy.shutdown()

if __name__ == '__main__':
    main()
